sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	//g_form.addInfoMessage("Start set email address");
   if ((isLoading && !g_form.isNewRecord()|| newValue === '')||(newValue === oldValue)) {
      return;
   }

	var getEmailAddr = new GlideAjax('GetEmailAddress');
	getEmailAddr.addParam('sysparm_name', 'getEmail');
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	getEmailAddr.getXML(populateEmail);

	function populateEmail(response){
		//g_form.addInfoMessage("Receive response - set email address");
		var ans = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email', ans);
		//g_form.addInfoMessage("End set email address");
	}

   //Type appropriate comment here, and begin script below

}
