sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		gs.addInfoMessage("Check 1");
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		if (testDate.before(rightNow)){
			return true;
		}
		else
			return false;
	},

	isDateToday: function(strDate){
		gs.addInfoMessage("Check 2");
		var today = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		var todayDate = today.getLocalDate();
		var neededDate = testDate.getLocalDate();

		if(neededDate.compareTo(todayDate) == 0){
			return true;
		}
		else
			return false;
	},

    type: 'NeedItUtils'
};
